import React from "react";

// Import images
import stock from "../img/image1.jpg";
import stock1 from "../img/image2.jpg";
import stock2 from "../img/image3.jpg";
import stock3 from "../img/image4.jpg";
import stock4 from "../img/image5.jpg";
import stock5 from "../img/image6.jpg";

// Define the projects and their corresponding URLs
const projects = [
  { name: "RAFT Distributed Consensus", url: "https://github.com/cbrownaz24/Raft-Distributed-Consensus", image: stock },
  { name: "Resume ArchTech", url: "https://github.com/cbrownaz24/ResumeArchtech", image: stock1 },
  { name: "Protein Dynamics Using Transformers", url: "https://github.com/cbrownaz24/PDT-FINAL", image: stock2 },
  { name: "Hidden Markov Models", url: "https://github.com/cbrownaz24/HMM-Lesson", image: stock3 },
  { name: "Programmable 16-Bit, Turing Complete CPU", url: "https://github.com/cbrownaz24/Programmable-CPU", image: stock4 },
  { name: "Speedy Histogram Using CUDA", url: "https://github.com/cbrownaz24/CUDA-Histogram", image: stock5 }
];

class Portfolio extends React.Component {
  render() {
    return (
      <section id="work" className="portfolio-mf sect-pt4 route">
        <div className="container">
          <div className="row">
            <div className="col-sm-12">
              <div className="title-box text-center">
                <h3 className="title-a">Portfolio</h3>
                <p className="subtitle-a">
                  Here's a subset of my projects. These are the ones I most enjoyed, and which are some of the largest.
                </p>
                <div className="line-mf"></div>
              </div>
            </div>
          </div>
          <div className="row">
            {/* Map through the projects and dynamically generate each card */}
            {projects.map((project, index) => (
              <div className="col-md-4" key={index}>
                <div className="work-box">
                  <a href={project.url} target="_blank" rel="noopener noreferrer">
                    <div className="work-img">
                      <img src={project.image} alt={project.name} className="img-fluid" />
                    </div>
                    <div className="work-content">
                      <div className="row">
                        <div className="col-sm-8">
                          <h2 className="w-title">{project.name}</h2>
                        </div>
                        <div className="col-sm-4">
                          <div className="w-like">
                            <span className="ion-ios-plus-outline"></span>
                          </div>
                        </div>
                      </div>
                    </div>
                  </a>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>
    );
  }
}

export default Portfolio;
